// src/App.js

import React from 'react';
import backgroundImage from './images/background.jpg'; // import the background image

const App = () => {

  return (
    <div style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center', minHeight: '100vh', display: 'flex' }}>
        <button style={{ fontSize: '24px' }}>Teams</button>
        <button style={{ fontSize: '24px' }}>Drivers</button>
    </div>
  );
};

export default App;
