// src/App.js

import React, { useState } from 'react';
import backgroundImage from './images/f1_logo.jpg'; // import the background image

const App = () => {
  const [isLeftHovered, setIsLeftHovered] = useState(false);
  const [isRightHovered, setIsRightHovered] = useState(false);

  const handleLeftHover = () => {
    setIsLeftHovered(true);
    setIsRightHovered(false);
  };

  const handleRightHover = () => {
    setIsLeftHovered(false);
    setIsRightHovered(true);
  };

  const handleHoverExit = () => {
    setIsLeftHovered(false);
    setIsRightHovered(false);
  };

  return (
    <div
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          flex: 1,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
        onMouseEnter={handleLeftHover}
        onMouseLeave={handleHoverExit}
      >
        <button
          style={{
            fontSize: '24px',
            backgroundColor: isLeftHovered ? 'lightgrey' : 'transparent',
          }}
        >
          Teams
        </button>
      </div>
      <div
        style={{
          flex: 1,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
        onMouseEnter={handleRightHover}
        onMouseLeave={handleHoverExit}
      >
        <button
          style={{
            fontSize: '24px',
            backgroundColor: isRightHovered ? 'lightblue' : 'transparent',
          }}
        >
          Drivers
        </button>
      </div>
    </div>
  );
};

export default App;
